import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Form, Button, Row, Col } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { Loader } from '../components/Loader';
import { Message } from '../components/Message';
import { listUsers } from '../store/actions/userActions';

const UserListScreen = ({history}) => {

    const [email, setEmail] = useState('');


    const dispatch = useDispatch()

    const userList = useSelector(state=>state.userList)
    const {loading, error , users}= userList



  useEffect(()=>{
  dispatch(listUsers())
  },[dispatch])


    return (
        <>
        <h1>Users</h1>
          {loading? <Loader/> : error ? <Message>{error} </Message> :(
            <table className="table table-striped table-hover table-bordered table-responsive table-sm ">
  <thead>
    <tr>
      <th scope="col">ID</th>
      <th scope="col">NAME</th>
      <th scope="col">EMAIL</th>
      <th scope="col">ADMIN</th>
      <th scope="col"></th>
    </tr>
  </thead>
  <tbody>
   {users.map(el=>(
    <tr key={el._id}>
      <th scope="row">1</th>
      <td>{el._id}</td>
      <td>{el.name}</td>
      <td> <a href={`mailto:${el.email}`}>{el.email}</a> </td>
      <td>{el.isAdmin ? (<i className="fas fa-check" style={{color:'green'}}></i>) : (<i className="fas fa-check" style={{color:'red'}}></i>) }</td>
      <td>
          <Link to={``} >jh</Link>
      </td>
    </tr>
   ))}

  </tbody>
</table>
          )}
        </>
    )
}

export default UserListScreen
